// In a real device scenario, we would use react-native-sqlite-storage
// import SQLite from 'react-native-sqlite-storage';

// Mock DB for UI demo purposes/Development
const mockDB = [
    { id: 1, timestamp: new Date(Date.now() - 100000).toISOString(), status: 'CRITICAL', diagnosis: 'Absent Breath Sounds', confidence: 0.92 },
    { id: 2, timestamp: new Date(Date.now() - 500000).toISOString(), status: 'STABLE', diagnosis: 'Normal', confidence: 0.98 },
    { id: 3, timestamp: new Date(Date.now() - 900000).toISOString(), status: 'URGENT', diagnosis: 'Wheezes Detected', confidence: 0.88 },
];

export const getPatients = async () => {
    // Simulate async DB call
    return new Promise((resolve) => {
        setTimeout(() => resolve(mockDB), 500);
    });
};

export const saveScan = async (diagnosis, confidence, status) => {
    const newRecord = {
        id: Math.floor(Math.random() * 10000),
        timestamp: new Date().toISOString(),
        status,
        diagnosis,
        confidence
    };
    mockDB.unshift(newRecord);
    return newRecord;
};
